import styled, { css } from "styled-components";

export const CartDropdownContainer = styled.div`
  ::-webkit-scrollbar {
    display: none;
  }

  display: flex;
  flex-direction: column;
  position: absolute;
  width: 240px;
  height: 340px;
  padding: 20px;
  border: 1px solid black;
  background-color: white;
  top: 80px;
  right: 0;
  z-index: 5;
`;
